    private void jAceptarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jAceptarActionPerformed
        this.jAlta.setEnabled(true);
        this.jBaja.setEnabled(true);
        this.jModificacion.setEnabled(true);
        this.jConsulta.setEnabled(true);
        this.jSalir.setEnabled(true);
         this.jAceptar.setEnabled(false);
        this.jCancelar.setEnabled(false);
    }//GEN-LAST:event_jAceptarActionPerformed

